 $(document).ready(function() {
     ajax_data();
     ajax_data2();
 });

 function ajax_data() {
     var fecha_inicio = $('input[name=fecha_inicio]').val();
     var fecha_final = $('input[name=fecha_final]').val();
     $.ajax({
         type: 'GET',
         url: '../VentasEfrain/totales_fechas_cpanel/',
         dataType: "json",
         data: {
             'fecha_inicio': fecha_inicio,
             'fecha_final': fecha_final,
         },
         cache: false,
         timeout: 20000,
         beforeSend: function(data) {},
         success: function(data) {
             charts(data);
         },
         error: function(data) {
             alert("Error In Connecting");
         }
     });
 }

 function charts(data) {
     var jsonData = data;
     google.load("visualization", "1", { packages: ["corechart"], callback: drawVisualization });

     function drawVisualization() {
         var data = new google.visualization.DataTable();
         data.addColumn('string', 'Fecha');
         data.addColumn('number', 'Reporte Ventas');
         data.addColumn('number', 'Reportes Gastos');
         data.addColumn('number', 'Utilidad');
         $.each(jsonData, function(i, jsonData) {
             var fecha = jsonData.fecha;
             var total_ingresos = jsonData.total_ingresos;
             var total_egresos = jsonData.total_egresos;
             var utilidad = jsonData.utilidad;
             data.addRows([
                 [fecha, total_ingresos, total_egresos, utilidad]
             ]);
         });
         var options = {
             title: "Fechas",
             animation: {
                 duration: 3000,
                 easing: 'out',
                 startup: true
             },
             colorAxis: { colors: ['#54C492', '#cc0000'] },
             datalessRegionColor: '#00FF00',
             defaultColor: '#dedede',
             series: {
                 0: { color: '#1493B6' },
                 1: { color: '#073441' },
             },
             backgroundColor: { fill: "#e2e3e5" },
         };
         var chart;
         chart = new google.visualization.LineChart(document.getElementById('container'));
         chart.draw(data, options);
     }
 }

 function ajax_data2() {
     $.ajax({
         type: 'POST',
         url: '../VentasEfrain/reporte_utilidad_cpanel/',
         dataType: "json",
         cache: false,
         timeout: 20000,
         beforeSend: function(data) {},
         success: function(data) {
             console.log(data);
             charts2(data);
         },
         error: function(data) {
             alert("Error In Connecting");
         }
     });
 }

 function charts2(data) {
     var jsonData = data;
     var ingresos = jsonData.ingresos;
     var egresos = jsonData.egresos;
     var utilidades = jsonData.utilidades;
     google.charts.load("current", { packages: ["corechart"] });
     google.charts.setOnLoadCallback(drawChart);

     function drawChart() {
         var datas = google.visualization.arrayToDataTable([
             ['Descripcion', 'Total'],
             ['INGRESOS', parseFloat(ingresos)],
             ['EGRESOS', parseFloat(egresos)],
             ['UTILIDADES', parseFloat(utilidades)]
         ]);
         var options = {
             title: 'Reporte Utilidad',
             is3D: true,
             chartArea: { width: 400, height: 400 },
             backgroundColor: { fill: "#e2e3e5" },
             animation: {
                 duration: 3000,
                 easing: 'out',
                 startup: true
             },
         };
         var chart = new google.visualization.PieChart(document.getElementById('container2'));
         chart.draw(datas, options);
     }
 }