$(document).ready(function() {
    verificar_caja();
});

function verificar_caja() {
    $.ajax({
        url: '../CajaNueva/verificar_estado',
        beforeSend: function() {},
        success: function(data) {
            switch (data) {
                case '0':
                    $('#form_abrir_caja').show();
                    $('#form_cerrar_caja').hide();
                    break;
                case '1':
                    $('#form_abrir_caja').hide();
                    $('#form_cerrar_caja').show();
                    break;
            }
        },
    });
}
$(function() {
    $("#form_abrir_caja").on("submit", function(e) {
        e.preventDefault();
        var f = $(this);
        var metodo = f.attr("method");
        var url = f.attr("action");
        var formData = new FormData(this);
        formData.append("dato", "valor");
        $.ajax({
            url: url,
            type: metodo,
            dataType: "html",
            data: formData,
            cache: false,
            contentType: false,
            processData: false,
            beforeSend: function() {
                alertify.notify('Agregando ...', 'custom-black', 4, function() {});
            },
            success: function(data) {
                location.reload('../Menu/registro_caja');
            },
            error: function() {},
        });
    });
});
